/**
 * Service Worker for Project Chimera
 * Handles SidePanel registration, context menu creation,
 * and message passing between components.
 */

// Register the side panel
chrome.sidePanel.setPanelBehavior({ openPanelOnActionClick: true });

// Set up context menu for selecting text to summarize
chrome.runtime.onInstalled.addListener(() => {
	chrome.contextMenus.create({
		id: "summarizeSelection",
		title: "Summarize Selection",
		contexts: ["selection"]
	});
});

// Handle context menu clicks
chrome.contextMenus.onClicked.addListener((info, tab) => {
	if (info.menuItemId === "summarizeSelection" && info.selectionText) {
		// Open the side panel
		chrome.sidePanel.open({ tabId: tab.id });

		// Send the selected text to the side panel
		// We'll use a setTimeout to ensure the panel is open before sending
		setTimeout(() => {
			chrome.runtime.sendMessage({
				action: "summarizeSelection",
				text: info.selectionText
			});
		}, 300);
	}
});

// Listen for messages from content script and side panel
chrome.runtime.onMessage.addListener((message, sender, sendResponse) => {
	// Will handle different message types here as we develop
	console.log("Service worker received message:", message);

	// Make sure to return true if we're going to send a response asynchronously
	return true;
});

/**
 * Content Script for Project Chimera
 *
 * Responsible for extracting content from web pages and
 * handling communication with the service worker.
 */

console.log('Content script loaded');

/**
 * Extracts the main content from the current webpage
 * using a multi-stage approach based on semantic HTML elements.
 *
 * @returns {string} The extracted page content
 */
function extractPageContent() {
	// Start with higher-priority semantic elements
	const mainElements = document.querySelectorAll('article, main, [role="main"]');
	if (mainElements.length > 0) {
		// Use the first main element's content
		return Array.from(mainElements)
			.map(el => el.innerText)
			.join('\n\n');
	}

	// Fallback to body content, but try to filter out navigation/footers
	const bodyContent = document.body.innerText;
	return bodyContent;
}

/**
 * Gets the page title and metadata
 *
 * @returns {object} Page metadata
 */
function getPageMetadata() {
	return {
		title: document.title,
		url: window.location.href,
		timestamp: new Date().toISOString(),
	};
}

/**
 * Sends the full page content to the service worker for summarization
 */
function summarizeFullPage() {
	const content = extractPageContent();
	const metadata = getPageMetadata();

	chrome.runtime.sendMessage({
		action: "summarizeFullPage",
		content: content,
		metadata: metadata
	});
}

// Listen for messages from the service worker
chrome.runtime.onMessage.addListener((message, sender, sendResponse) => {
	console.log("Content script received message:", message);

	if (message.action === 'extractPageContent') {
		// Simple content extraction for now
		const content = document.body.innerText;
		const title = document.title;
		const url = window.location.href;

		sendResponse({
			content: content,
			metadata: {
				title: title,
				url: url,
				timestamp: new Date().toISOString()
			}
		});
	}

	// Return true to indicate we'll send a response asynchronously
	return true;
});

// This will be expanded with more sophisticated content extraction in the future
console.log("Project Chimera content script loaded");

{
  "name": "Project Chimera - AI Page Summarizer",
  "description": "Summarize web pages using AI directly from Chrome's side panel",
  "version": "1.0.0.30",
  "manifest_version": 3,
  "icons": {
    "16": "assets/icons/icon16.png",
    "48": "assets/icons/icon48.png",
    "128": "assets/icons/icon128.png"
  },
  "permissions": [
    "sidePanel",
    "contextMenus",
    "storage",
    "commands",
    "tts"
  ],
  "host_permissions": [
    "<all_urls>"
  ],
  "background": {
    "service_worker": "background/serviceWorker.js",
    "type": "module"
  },
  "side_panel": {
    "default_path": "sidepanel/index.html"
  },
  "content_scripts": [
    {
      "matches": [
        "<all_urls>"
      ],
      "js": [
        "content/content-script.js"
      ]
    }
  ],
  "action": {
    "default_title": "Open AI Summarizer"
  },
  "commands": {
    "_execute_action": {
      "suggested_key": {
        "default": "Alt+S"
      },
      "description": "Open AI Summarizer"
    }
  }
}

/**
 * API Client for Project Chimera
 * Handles communication with OpenAI API
 */

/**
 * Generate a summary using OpenAI's API
 *
 * @param {string} content - The text content to summarize
 * @param {object} options - Summarization options
 * @param {string} options.format - The summary format (bullets, academic, professional, simplified)
 * @param {string} options.length - The summary length (brief, detailed)
 * @param {object} options.feedback - AI feedback options
 * @param {boolean} options.feedback.enableToneBiasAnalysis - Whether to include tone and bias analysis
 * @param {boolean} options.feedback.enableHighlightVagueClaims - Whether to highlight vague claims
 * @param {boolean} options.feedback.enableCounterpoints - Whether to present counterpoints
 * @param {boolean} options.feedback.enableSentimentDetection - Whether to detect sentiment
 * @param {boolean} options.feedback.enableIntentSummary - Whether to summarize intent
 * @param {boolean} options.feedback.enableFactContrast - Whether to contrast with known facts
 * @param {string} apiKey - The OpenAI API key
 * @returns {Promise<string>} - The generated summary
 */
export async function generateSummary(content, options, apiKey) {
	try {
		console.log('API Client: Generating summary with options:', options);

		if (!apiKey) {
			throw new Error('API key is required');
		}

		// Limit content length to prevent excessive token usage
		const maxContentLength = 15000;
		const truncatedContent = content.length > maxContentLength
			? content.substring(0, maxContentLength) + "...(content truncated for token efficiency)"
			: content;

		// Generate system prompt based on options
		const systemPrompt = getSystemPrompt(options);

		// Make the actual fetch request to OpenAI API
		const response = await fetch('https://api.openai.com/v1/chat/completions', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
				'Authorization': `Bearer ${apiKey}`
			},
			body: JSON.stringify({
				model: 'gpt-3.5-turbo',
				messages: [
					{
						role: 'system',
						content: systemPrompt
					},
					{
						role: 'user',
						content: truncatedContent
					}
				],
				temperature: 0.5 // Lower temperature for more consistent summaries
			})
		});

		if (!response.ok) {
			const errorData = await response.json().catch(() => null);
			console.error('API error response:', errorData);
			throw new Error(`API request failed: ${response.status} ${response.statusText}`);
		}

		const data = await response.json();
		return data.choices[0].message.content;

	} catch (error) {
		console.error('Error generating summary:', error);
		throw error;
	}
}

/**
 * Generate system prompt based on format and length options
 *
 * @param {object} options - The summary options
 * @returns {string} - The system prompt
 */
function getSystemPrompt(options) {
	const { format, length, feedback = {} } = options;

	// Base instructions
	let prompt = `You are an AI assistant specialized in summarizing content. `;

	// Length-specific instructions
	if (length === 'brief') {
		prompt += `Create a concise summary that captures the main points in about 3-5 short paragraphs. `;
	} else if (length === 'detailed') {
		prompt += `Create a comprehensive summary that covers all significant points and details in about 5-7 paragraphs. `;
	}

	prompt += `Do not include any personal opinions or subjective statements. Focus solely on the content provided. `;

	// Format-specific instructions
	if (format === 'bullets') {
		prompt += `Format your response as a bulleted list of key points, with a very brief introduction. Use ‚Ä¢ as bullet points. Be direct and clear. `;
	} else if (format === 'academic') {
		prompt += `Format your response in an academic style with formal language, clear structure, and objective analysis. Include an introduction, body paragraphs, and conclusion. `;
	} else if (format === 'professional') {
		prompt += `Format your response as a professional executive summary with clear sections, factual statements, and actionable insights. Keep the tone neutral and the language precise. `;
	} else if (format === 'simplified') {
		prompt += `Format your response in simple, easy-to-understand language. Avoid complex terminology, use shorter sentences, and explain concepts clearly as if to someone with limited background knowledge. `;
	}

	// AI Feedback & Impressions options
	const feedbackRequested = Object.values(feedback).some(value => value === true);

	if (feedbackRequested) {
		prompt += `\n\nAfter providing the summary, please include an "ADDITIONAL ANALYSIS" section with the following components:\n`;

		if (feedback.enableToneBiasAnalysis) {
			prompt += `\n<h3>Tone and Bias Analysis</h3>\n<div class="analysis-section">Analyze the tone of the content (formal, casual, persuasive, informative, etc.) and identify any potential biases or slants in the presentation. Consider word choice, framing, and what information is emphasized or omitted. Format this as 2-3 paragraphs.</div>\n`;
		}

		if (feedback.enableHighlightVagueClaims) {
			prompt += `\n<h3>Unsubstantiated or Vague Claims</h3>\n<div class="analysis-section vague-claims-section">
			<p>I've identified the following vague or unsubstantiated claims in the content:</p>
			<div class="claims-list">
			`;

			// Enhanced vague claims detection instructions
			prompt += `For each vague claim you identify:
			1. <div class="claim-item">
			   <div class="claim-text"><span class="vague-claim">Quote the exact claim text</span></div>
			   <div class="claim-type">Specify the type of issue (Unverifiable, Overgeneralization, Ambiguous language, Missing context, etc.)</div>
			   <div class="claim-confidence">Indicate confidence level (High, Medium, Low)</div>
			   <div class="claim-explanation">Explain briefly why this claim is problematic</div>
			   <div class="claim-improvement">Suggest how the claim could be improved</div>
			   </div>

			Aim to identify 3-5 of the most significant vague or unsubstantiated claims in the content.
			Format each claim as a separate div with the class "claim-item".
			If there are no significant vague claims, clearly state this fact.
			</div></div>\n`;
		}

		if (feedback.enableCounterpoints) {
			prompt += `\n<h3>Alternative Perspectives</h3>\n<div class="analysis-section">Present reasonable counterpoints or alternative viewpoints that might not be adequately addressed in the content. Format this as 3-4 bullet points.</div>\n`;
		}

		if (feedback.enableSentimentDetection) {
			prompt += `\n<h3>Entity Sentiment Analysis</h3>\n<div class="analysis-section">Identify key entities (people, organizations, concepts) mentioned in the content and analyze the sentiment expressed toward each. Format as a brief list with <span class="entity-positive">positive</span>, <span class="entity-neutral">neutral</span>, or <span class="entity-negative">negative</span> indicators for each entity.</div>\n`;
		}

		if (feedback.enableIntentSummary) {
			prompt += `\n<h3>Content Intent Analysis</h3>\n<div class="analysis-section">Briefly analyze the likely intent or purpose of the content (to inform, persuade, entertain, sell, etc.) and the underlying message. One paragraph maximum.</div>\n`;
		}

		if (feedback.enableFactContrast) {
			prompt += `\n<h3>Fact Checking Notes</h3>\n<div class="analysis-section">Identify statements that could benefit from fact-checking or additional context, and note any commonly accepted facts that might contradict claims in the content. Format as bullet points.</div>\n`;
		}
	}

	// Feedback options
	if (feedback) {
		if (feedback.enableToneBiasAnalysis) {
			prompt += `Analyze the tone and potential bias in the content. `;
		}
		if (feedback.enableHighlightVagueClaims) {
			prompt += `Highlight any vague or ambiguous claims. `;
		}
		if (feedback.enableCounterpoints) {
			prompt += `Present potential counterpoints to the claims made. `;
		}
		if (feedback.enableSentimentDetection) {
			prompt += `Detect and summarize the sentiment expressed in the content. `;
		}
		if (feedback.enableIntentSummary) {
			prompt += `Summarize the intent behind the content. `;
		}
		if (feedback.enableFactContrast) {
			prompt += `Contrast the content with known facts or common knowledge. `;
		}
	}

	prompt += `Do not use any markdown, only HTML formatting if required. `;
	prompt += `<b> for bold text, <i> for italic text, <u> for underlined text, <p> for paragraphs, <br> for line breaks, <h3> for section headings. `;
	prompt += `Avoid using any other HTML tags, this is a partial HTML document.`;

	return prompt;
}

/**
 * Storage Manager for Project Chimera
 * Handles persistent storage operations
 */

/**
 * Save a summary to history
 *
 * @param {Object} summaryData - The summary data to save
 * @param {string} summaryData.content - The summary text content
 * @param {Object} summaryData.metadata - Metadata about the summary
 * @param {string} summaryData.metadata.title - Page title
 * @param {string} summaryData.metadata.url - Page URL
 * @param {string} summaryData.metadata.timestamp - When the summary was created
 * @param {Object} summaryData.options - Summary generation options
 * @returns {Promise<void>}
 */
export async function saveSummaryToHistory(summaryData) {
	try {
		// Get existing history
		const data = await chrome.storage.local.get(['summaryHistory']);
		const history = data.summaryHistory || [];

		// Add new summary to the beginning of the array (newest first)
		history.unshift(summaryData);

		// Limit history to 50 items to prevent excessive storage usage
		const limitedHistory = history.slice(0, 50);

		// Save back to storage
		await chrome.storage.local.set({ summaryHistory: limitedHistory });

		return true;
	} catch (error) {
		console.error('Error saving summary to history:', error);
		return false;
	}
}

/**
 * Get all summary history
 *
 * @returns {Promise<Array>} Array of summary history items
 */
export async function getSummaryHistory() {
	try {
		const data = await chrome.storage.local.get(['summaryHistory']);
		return data.summaryHistory || [];
	} catch (error) {
		console.error('Error getting summary history:', error);
		return [];
	}
}

/**
 * Delete a summary from history by index
 *
 * @param {number} index - The index of the summary to delete
 * @returns {Promise<boolean>} Success status
 */
export async function deleteSummaryFromHistory(index) {
	try {
		const data = await chrome.storage.local.get(['summaryHistory']);
		const history = data.summaryHistory || [];

		if (index >= 0 && index < history.length) {
			history.splice(index, 1);
			await chrome.storage.local.set({ summaryHistory: history });
			return true;
		}
		return false;
	} catch (error) {
		console.error('Error deleting summary from history:', error);
		return false;
	}
}

/**
 * Clear all summary history
 *
 * @returns {Promise<boolean>} Success status
 */
export async function clearSummaryHistory() {
	try {
		await chrome.storage.local.set({ summaryHistory: [] });
		return true;
	} catch (error) {
		console.error('Error clearing summary history:', error);
		return false;
	}
}

:root {
	--bg-primary: #ffffff;
	--bg-secondary: #f5f7fa;
	--text-primary: #333333;
	--text-secondary: #666666;
	--accent-color: #4f6df5;
	--accent-hover: #3b5bd9;
	--border-color: #e0e0e0;
	--error-color: #e53935;
	--success-color: #43a047;

	--spacing-xs: 4px;
	--spacing-sm: 8px;
	--spacing-md: 16px;
	--spacing-lg: 24px;
	--spacing-xl: 32px;
	--border-radius: 4px;

	--font-family: 'Outfit', system-ui, -apple-system, sans-serif;
}

[data-theme="dark"] {
	--bg-primary: #1e1e1e;
	--bg-secondary: #2d2d2d;
	--text-primary: #e0e0e0;
	--text-secondary: #b0b0b0;
	--border-color: #3d3d3d;
}

* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
}

body {
	font-family: var(--font-family);
	background-color: var(--bg-primary);
	color: var(--text-primary);
	font-size: 14px;
	line-height: 1.5;
	margin: 0;
	padding: 0;
	min-width: 300px;
	transition: background-color 0.3s ease;
}

.app-container {
	display: flex;
	flex-direction: column;
	height: 100vh;
	max-width: 100%;
}

.app-header {
	padding: var(--spacing-md);
	border-bottom: 1px solid var(--border-color);
	background-color: var(--bg-primary);
}

.app-header h1 {
	font-size: 18px;
	font-weight: 500;
	margin: 0;
}

.tab-navigation {
	display: flex;
	border-bottom: 1px solid var(--border-color);
	background-color: var(--bg-primary);
}

.tab-button {
	flex: 1;
	padding: var(--spacing-md);
	background: none;
	border: none;
	cursor: pointer;
	color: var(--text-secondary);
	font-weight: 500;
	transition: color 0.2s, border-bottom 0.2s;
}

.tab-button.active {
	color: var(--accent-color);
	border-bottom: 2px solid var(--accent-color);
}

.tab-button:hover:not(.active) {
	color: var(--text-primary);
}

.tab-button:focus {
	outline: none;
	background-color: rgba(79, 109, 245, 0.05);
}

.tab-content {
	flex: 1;
	overflow-y: auto;
	padding: var(--spacing-md);
}

.tab-panel {
	display: none;
}

.tab-panel.active {
	display: block;
}

.controls-container {
	display: flex;
	flex-wrap: wrap;
	gap: var(--spacing-md);
	margin-bottom: var(--spacing-md);
}

.format-controls,
.length-controls {
	min-width: 140px;
}

label {
	display: block;
	margin-bottom: var(--spacing-xs);
	color: var(--text-secondary);
	font-size: 13px;
}

select {
	width: 100%;
	padding: 8px;
	border: 1px solid var(--border-color);
	border-radius: var (--border-radius);
	background-color: var(--bg-primary);
	color: var(--text-primary);
}

.primary-button {
	width: 100%;
	padding: var(--spacing-sm);
	background-color: var(--accent-color);
	color: white;
	border: none;
	border-radius: var(--border-radius);
	cursor: pointer;
	font-weight: 500;
	transition: background-color 0.2s;
}

.primary-button:hover {
	background-color: var(--accent-hover);
}

.primary-button:focus {
	outline: none;
	box-shadow: 0 0 0 2px rgba(79, 109, 245, 0.3);
}

.summary-result-container {
	margin-top: var(--spacing-lg);
	border-radius: var(--border-radius);
	background-color: var(--bg-secondary);
	min-height: 100px;
}

#summary-content {
	padding: var(--spacing-md);
}

#summary-content.hidden {
	display: none;
}

.summary-header {
	display: flex;
	justify-content: space-between;
	align-items: center;
	margin-bottom: var(--spacing-md);
}

.summary-header h2 {
	font-size: 16px;
	font-weight: 500;
	margin: 0;
}

.summary-actions button {
	background: none;
	border: none;
	cursor: pointer;
	padding: var(--spacing-xs);
	color: var(--text-secondary);
	border-radius: var(--border-radius);
}

.summary-actions button:hover {
	color: var(--text-primary);
	background-color: var(--bg-primary);
}

#summary-text {
	line-height: 1.6;
	white-space: pre-wrap;
}

#loading-indicator {
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	padding: var(--spacing-xl);
}

#loading-indicator.hidden {
	display: none;
}

.spinner {
	width: 30px;
	height: 30px;
	border: 3px solid rgba(79, 109, 245, 0.3);
	border-radius: 50%;
	border-top-color: var(--accent-color);
	animation: spin 1s linear infinite;
	margin-bottom: var(--spacing-md);
}

@keyframes spin {
	0% {
		transform: rotate(0deg);
	}

	100% {
		transform: rotate(360deg);
	}
}

.settings-group {
	margin-bottom: var(--spacing-lg);
}

.settings-group h3 {
	font-size: 15px;
	margin-bottom: var(--spacing-md);
	color: var(--text-primary);
}

.setting-item {
	margin-bottom: var(--spacing-md);
}

.setting-description {
	font-size: 12px;
	color: var(--text-secondary);
	margin-top: var(--spacing-xs);
}

input[type="password"],
input[type="text"] {
	width: 100%;
	padding: 8px;
	border: 1px solid var(--border-color);
	border-radius: var(--border-radius);
	background-color: var(--bg-primary);
	color: var(--text-primary);
}

.toggle-switch {
	position: relative;
	display: inline-block;
	width: 44px;
	height: 24px;
	vertical-align: middle;
	margin-right: var(--spacing-md);
}

.toggle-switch input {
	opacity: 0;
	width: 0;
	height: 0;
}

.toggle-slider {
	position: absolute;
	cursor: pointer;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background-color: var(--border-color);
	transition: 0.4s;
	border-radius: 34px;
}

.toggle-slider:before {
	position: absolute;
	content: "";
	height: 18px;
	width: 18px;
	left: 3px;
	bottom: 3px;
	background-color: white;
	transition: 0.4s;
	border-radius: 50%;
}

input:checked+.toggle-slider {
	background-color: var(--accent-color);
}

input:checked+.toggle-slider:before {
	transform: translateX(20px);
}

.search-container {
	margin-bottom: var(--spacing-md);
}

.search-container input {
	width: 100%;
	padding: 8px 12px;
	border: 1px solid var(--border-color);
	border-radius: var(--border-radius);
}

#error-message {
	padding: var(--spacing-md);
	color: var (--error-color);
}

#error-message.hidden {
	display: none;
}

.error-actions {
	display: flex;
	gap: var(--spacing-md);
	margin-top: var(--spacing-md);
}

.secondary-button {
	padding: var(--spacing-sm) var(--spacing-sm);
	background-color: var(--bg-primary);
	color: var(--text-primary);
	border: 1px solid var(--border-color);
	border-radius: var(--border-radius);
	cursor: pointer;
	font-weight: 500;
	transition: background-color 0.2s;
}

.secondary-button:hover {
	background-color: var(--bg-secondary);
}

.app-footer {
	padding: var(--spacing-sm);
	border-top: 1px solid var(--border-color);
	text-align: center;
	font-size: 12px;
	color: var(--text-secondary);
}


.history-actions {
	display: flex;
	justify-content: flex-end;
	margin-bottom: var(--spacing-md);
}

.history-item {
	background-color: var(--bg-secondary);
	border-radius: var(--border-radius);
	padding: var(--spacing-md);
	margin-bottom: var(--spacing-md);
}

.history-item-header {
	display: flex;
	justify-content: space-between;
	align-items: flex-start;
	margin-bottom: var(--spacing-xs);
}

.history-item-title {
	font-size: 15px;
	font-weight: 500;
	margin: 0;
	line-height: 1.3;
	flex: 1;
	cursor: pointer;
	padding: var(--spacing-xs) 0;
}

.history-item-title:hover {
	color: var(--accent-color);
}

.history-item-actions {
	display: flex;
	gap: var(--spacing-xs);
}

.history-item-actions button {
	background: none;
	border: none;
	cursor: pointer;
	padding: 4px;
	color: var(--text-secondary);
	border-radius: var(--border-radius);
}

.history-item-actions button:hover {
	color: var(--text-primary);
	background-color: var(--bg-primary);
}

.history-item-meta {
	display: flex;
	justify-content: space-between;
	font-size: 12px;
	color: var(--text-secondary);
	margin-bottom: var(--spacing-xs);
}

.history-item-url {
	font-size: 12px;
	color: var(--accent-color);
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
	margin-bottom: var(--spacing-sm);
